// Company Order Page
const CompanyPage = () => {
  const { name } = useParams();
  const company = partnerCompanies.find(c => c.name === name);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [address, setAddress] = useState("");
  const [contact, setContact] = useState("");
  const [paymentMethod, setPaymentMethod] = useState("Credit Card");
  const [cardDetails, setCardDetails] = useState({ cardNumber: "", name: "", expiry: "", cvv: "" });

  if (!company) return <div className="pt-24 p-6 text-white">Company not found</div>;

  const isExpiryValid = (expiry) => {
    if (!expiry.includes("/")) return false;
    const [monthStr, yearStr] = expiry.split("/");
    const month = parseInt(monthStr);
    let year = parseInt(yearStr);
    if (yearStr.length === 2) year += 2000;
    const today = new Date();
    return new Date(year, month, 0) >= today;
  };

  const handlePlaceOrder = () => {
    const totalPrice = selectedProduct.price * quantity;
    if ((paymentMethod === "Credit Card" || paymentMethod === "Debit Card") && !isExpiryValid(cardDetails.expiry)) {
      alert("Card expiry invalid!");
      return;
    }
    alert(`Order Details:
Product: ${selectedProduct.name}
Quantity: ${quantity}
Total: ₹${totalPrice}
Address: ${address}
Contact: ${contact}`);
    setSelectedProduct(null); setQuantity(1); setAddress(""); setContact(""); setPaymentMethod("Credit Card"); setCardDetails({ cardNumber: "", name: "", expiry: "", cvv: "" });
  };

  return (
    <div className="pt-24 p-6 text-white bg-gray-900 min-h-screen">
      <button className="mb-4 bg-gray-700 px-4 py-2 rounded hover:bg-gray-600" onClick={() => window.history.back()}>← Back</button>
      <h2 className="text-2xl font-bold mb-4">{company.name} Products</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        {company.products.map((product, idx) => (
          <div
            key={idx}
            onClick={() => setSelectedProduct(product)}
            className="bg-gray-700 p-4 rounded shadow cursor-pointer hover:bg-gray-600 flex flex-col items-center"
          >
            <h3 className="text-lg font-semibold">{product.name}</h3>
            <p className="text-gray-300 mt-2">₹{product.price}</p>
          </div>
        ))}
      </div>

      {selectedProduct && (
        <div className="max-w-md mx-auto bg-gray-800 p-6 rounded shadow mb-8">
          <button className="mb-4 bg-gray-700 px-4 py-2 rounded hover:bg-gray-600" onClick={() => setSelectedProduct(null)}>← Back</button>
          <h3 className="text-2xl font-bold mb-4">{selectedProduct.name}</h3>
          <p className="mb-4">Price per unit: ₹{selectedProduct.price}</p>

          <label className="block mb-2">Quantity:</label>
          <input type="number" min="1" value={quantity} onChange={e => setQuantity(Number(e.target.value))} className="w-full p-2 mb-4 rounded bg-gray-700 text-white" />

          <label className="block mb-2">Address:</label>
          <textarea value={address} onChange={e => setAddress(e.target.value)} className="w-full p-2 mb-4 rounded bg-gray-700 text-white" />

          <label className="block mb-2">Contact:</label>
          <input type="text" value={contact} onChange={e => setContact(e.target.value)} className="w-full p-2 mb-4 rounded bg-gray-700 text-white" />

          <label className="block mb-2">Payment Method:</label>
          <select value={paymentMethod} onChange={e => setPaymentMethod(e.target.value)} className="w-full p-2 mb-4 rounded bg-gray-700 text-white">
            <option>Credit Card</option>
            <option>Debit Card</option>
            <option>COD</option>
          </select>

          {(paymentMethod === "Credit Card" || paymentMethod === "Debit Card") && (
            <div className="mb-4">
              <input type="text" placeholder="Card Number" value={cardDetails.cardNumber} onChange={e => setCardDetails({ ...cardDetails, cardNumber: e.target.value })} className="w-full p-2 mb-2 rounded bg-gray-700 text-white"/>
              <input type="text" placeholder="Name on Card" value={cardDetails.name} onChange={e => setCardDetails({ ...cardDetails, name: e.target.value })} className="w-full p-2 mb-2 rounded bg-gray-700 text-white"/>
              <div className="flex gap-2">
                <input type="text" placeholder="Expiry (MM/YY)" value={cardDetails.expiry} onChange={e => setCardDetails({ ...cardDetails, expiry: e.target.value })} className="w-1/2 p-2 mb-2 rounded bg-gray-700 text-white"/>
                <input type="text" placeholder="CVV" value={cardDetails.cvv} onChange={e => setCardDetails({ ...cardDetails, cvv: e.target.value })} className="w-1/2 p-2 mb-2 rounded bg-gray-700 text-white"/>
              </div>
            </div>
          )}

          <p className="mb-4 font-semibold">Total Price: ₹{selectedProduct.price * quantity}</p>
          <button onClick={handlePlaceOrder} className="w-full bg-blue-600 py-2 rounded hover:bg-blue-700">Place Order</button>
        </div>
      )}
    </div>
  );
};

// App Component
const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Dashboard />} />
        <Route path="/company/:name" element={<CompanyPage />} />
      </Routes>
    </Router>
  );
};

export default App;
